  `content` text COLLATE utf8mb4_bin NOT NULL COMMENT '文章内容',
  `posids` varchar(255) COLLATE utf8mb4_bin NOT NULL DEFAULT '' COMMENT '推荐位',
  `sort` smallint(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `allow_comment` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否允许评论',
  `views` int(10) NOT NULL DEFAULT '0' COMMENT '浏览数',
  `create_at` int(10) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `author` char(20) COLLATE utf8mb4_bin NOT NULL DEFAULT '' COMMENT '文章作者',
  `comefrom` char(20) COLLATE utf8mb4_bin NOT NULL DEFAULT '' COMMENT '文章来源',
  `tags` varchar(150) COLLATE utf8mb4_bin NOT NULL DEFAULT '' COMMENT '标签',
  `modelid` tinyint(5) NOT NULL DEFAULT '0' COMMENT '模型ID',
  `is_deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除(1删除,0未删)',
  PRIMARY KEY (`id`),
  KEY `status` (`id`),
  KEY `listorder` (`id`),
  KEY `catid` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=253 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

